installer-script.script:
function pm_install_check() {
    if(!(system.compareVersions(system.version.ProductVersion,'10.13') >= 0)) {
        my.result.title = 'Version du système incompatible';
        my.result.message = 'Ce package nécessite macOS 10.13 ou version ultérieure.';
        my.result.type = 'Fatal';
        return false;
    }
    return true;
}
